  }

  //Clear text....................
  const clearText = () => {
    let clearTxt = "";
    setText(clearTxt);
    props.showAlert("All text is deleted. ","success");
  };
  // Copytext......................
  const copyText = () => {
    var textCopy = document.getElementById("myBox");
    textCopy.select();
    navigator.clipboard.writeText(textCopy.value);
    props.showAlert("Text is copied ","success");
  };
  //Paste Text.........................
  const pasteText = () => {
    let textCopy = text;
    setText(text +"\n"+ textCopy);
    props.showAlert("Text is pasted  ","success");
  };
  const [text, setText] = useState("Enter your here");

  return (
    <div
      className="container-fluid"
      style={{ color: props.mode === "dark" ? "white" : "black" }}
    >
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea
          className="form-control"
          value={text}
          onChange={handleOnChange}
          id="myBox"
          rows="8"
          style={{
            background: props.mode === "dark" ? "grey" : "white",
            color: props.mode === "dark" ? "white" : "black",
          }}
        />
      </div>
      <div
        className="container-fluid"
        style={{ color: props.mode === "dark" ? "white" : "black" }}
        align="center"
      >
        <button className="btn btn-primary mx-2" onClick={clickToUpperCase}>
          Covert to UpperCase
        </button>
        <button className="btn btn-primary mx-2" onClick={clickToLowerCase}>
          Convert to LowerCase
        </button>

        <button className="btn btn-primary mx-2" onClick={clearText}>
          Clear
        </button>
        <button className="btn btn-primary " onClick={copyText}>
          {" "}
          Copy
        </button>
        <button className="btn btn-primary mx-2" onClick={pasteText}>
          {" "}
          Paste
        </button>

        <button className="btn btn-primary" onClick={clickToCamelCase}> Convert to CamelCase</button>
        
      </div>
      <div
        className="container"
        style={{
          background: props.mode === "dark" ? "light" : "dark",
          color: props.mode === "dark" ? "white" : "black",
        }}
      >
        <h2>Text Summary</h2>
        <p>
          {text.split(" ").length} Words and {text.length} Characters
        </p>
        <p>{0.008 * text.split(" ").length} Time Taken To Read </p>
        <p>{text.split(".").length} Sentences</p>
        <h2>Preview Text</h2>
        <p>{text}</p>
      </div>
    </div>
  );
}
export default TextForm;
