import React, {useState} from "react";



function TextForm(props) {

  const clickToUpperCase=() =>{
       let newText=text.toUpperCase();
       setText(newText)

  }
  const handleOnChange=(event)=>{
    setText(event.target.value)
  }
  const clickToLowerCase=()=>{
    let lowerText=text.toLowerCase();
    setText(lowerText)
  }

  const clickToCamelCase=()=>{
    let camelText=text.replace(/\W+(.)/g,(match,char));
    setText(camelText)
  }
  const [text,setText]=useState('Enter your her');
 
  return (
    <div>
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8" />
       
</div>
<div className="container" align="center">
<button className="btn btn-primary" onClick={clickToUpperCase}>Covert to UpperCase</button>
<button className="btn btn-primary" onClick={clickToLowerCase}> Convert to LowerCase</button>

<button className="btn btn-primary" onClick={clickToCamelCase}> Convert to CamelCase</button>
          
</div>
</div>
  );
}
export default TextForm;
