import React, { useState } from "react";

function TextForm(props) {
  //Upper Casing Method...............
  const clickToUpperCase = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };

  const handleOnChange = (event) => {
    setText(event.target.value);
  };

  //  Lower Casing Method...............
  const clickToLowerCase = () => {
    let lowerText = text.toLowerCase();
    setText(lowerText);
  };
  //Camel Casing Method...............
  // const clickToCamelCase=()=>{
  //   let camelText=text.replace(/\W+(.)/g,);
  //   setText(camelText)
  // }

  //Clear text....................
  const clearText = () => {};
  const [text, setText] = useState("Enter your here");

  return (
    <div>
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea
          className="form-control"
          value={text}
          onChange={handleOnChange}
          id="myBox"
          rows="8"
        />
      </div>
      <div className="container-fluid px-4" align="center">
      <div className="row">
         <div> <button className="btn btn-primary px-4" onClick={clickToUpperCase}>
            Covert to UpperCase
          </button></div>
       
       
         <div> <button className="btn btn-primary my-2" onClick={clickToLowerCase}>
            Convert to LowerCase
          </button></div>
       <div>
       <button className="btn btn-primary my-3" onClick={clearText}>
          Clear
        </button>
       </div>
        
        {/* <button className="btn btn-primary" onClick={clickToCamelCase}> Convert to CamelCase</button>
         */}
         </div>
      </div>
      <div className="container">
        <h2>Text Summary</h2>
        <p>{text.split(" ").length}</p>
      </div>
    </div>
  );
}
export default TextForm;
