import React, { useState } from "react";

function TextForm(props) {
  //Upper Casing Method...............
  const clickToUpperCase = () => {
    let newText = text.toUpperCase();
    setText(newText);
  };

  const handleOnChange = (event) => {
    setText(event.target.value);
  };

  //  Lower Casing Method...............
  const clickToLowerCase = () => {
    let lowerText = text.toLowerCase();
    setText(lowerText);
  };
  //Camel Casing Method...............
  // const clickToCamelCase=()=>{
  //   let camelText=text.replace(/\W+(.)/g,);
  //   setText(camelText)
  // }

  //Clear text....................
  const clearText = () => {
    let clearTxt = "";
    setText(clearTxt);
  };
  // Copytext......................
  const copyText=()=>{
    let textCopy=document.getElementById("myBox");
    textCopy.select();
    navigator.clipboard.writeText(textCopy.value)

   
  };
  //Paste Text.........................
  const pasteText=()=>{
    let textCopy=text;
     setText(text+textCopy);
  };
  const [text, setText] = useState("Enter your here");

  return (
    <div>
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea
          className="form-control"
          value={text}
          onChange={handleOnChange}
          id="myBox"
          rows="8"
        />
      </div>
      <div className="container-fluid" align="center">
        <button className="btn btn-primary mx-2" onClick={clickToUpperCase}>
          Covert to UpperCase
        </button>
        <button className="btn btn-primary mx-2" onClick={clickToLowerCase}>
          Convert to LowerCase
        </button>

        <button className="btn btn-primary mx-2" onClick={clearText}>
          Clear
        </button>
        <button className="btn btn-primary " onClick={copyText}> Copy</button>
        <button className="btn btn-primary mx-2" onClick={pasteText}> Paste</button>

        {/* <button className="btn btn-primary" onClick={clickToCamelCase}> Convert to CamelCase</button>
         */}
      </div>
      <div className="container">
        <h2>Text Summary</h2>
        <p>
          {text.split(" ").length} Words and {text.length} Characters
        </p>
        <p>{0.008 * text.split(" ").length} Time Taken To Read </p>
        <p>{text.split(".").length} Sentences</p>
      </div>
    </div>
  );
}
export default TextForm;
